import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    align-items: center;
    background-color: #20212C;
    padding: 10px;
    border-radius: 10px;
    margin: 10px;

    input {
        width: 25px; height:25px;
        margin-right: 5px;
    }

    label {
        color: #ccc;
    }

`;
